insert into plant (name, description, locations, sun_level, gardening_level, forbidden_for_children,
                   forbidden_for_animals, temperature, image, size)
values ('Ficus Elastica', 'rois fois rien ! Cette plante d''intérieur facile d’entretien ne demande que très peu d’attention ! Le rempotage ne s’impose qu’une fois tous les 2 à 3 ans à l’âge adulte. Toutes les semaines, nettoyez ses grandes feuilles avec un chiffon humide afin d’ôter la poussière et de révéler son éclat. Pour une belle croissance, apportez-lui de l’engrais 1 ou 2 fois par mois au printemps et en été.', 'INSIDE', 'PARTIAL_SHADE', 1, false, false, 'NORMAL',
        'https://bergamotte.imgix.net/JkrqQUaaASi4DUZKzeecLrA4?ixlib=rails-4.2.0&auto=format%2Ccompress&fit=crop&q=65&ar=1%3A1&w=2048', 1);

