insert into plant (name, description, locations, sun_level, gardening_level, forbidden_for_children,
                   forbidden_for_animals, temperature, image, size)
values ('Dracaena', 'Le dracaena nous vient des îles, il porte aussi le nom d''arbre du dragon, de dragonnier ou encore de sang de dragon, à cause de la couleur rouge de sa sève. Sur l''île de Ténérife (une des île Canaries) pousse le célèbre dragonnier d''Icod de los Vinos, devenu un arbre légendaire. Le dracaena est le symbole de la défense.', 'INSIDE', 'PARTIAL_SHADE', 1, false, false, 'NORMAL',
        'https://cdn.shopify.com/s/files/1/0061/6389/0289/products/DracaenaMarginata_x700.jpg?v=1616558857', 1);